	BOARD_NO = #{boardNo}

UPDATE
	TB_CARD_MENU
SET
	CARD_SJ = #{cardSj}
WHERE
	BOARD_NO = #{boardNo}
	AND CARD_MENU_NO = #{cardMenuNo}

mapper.delete:
DELETE
FROM
	TB_CARD_MENU
WHERE
	BOARD_NO = #{boardNo}
	AND CARD_MENU_NO = #{cardMenuNo}

mapper.insert:

	SELECT NVL(MAX(CARD_NO), 0) + 1 AS cardNo FROM TB_CARD

INSERT
	INTO
	TB_CARD
(CARD_MENU_NO
	, BOARD_NO
	, CARD_NO
	, CARD_NM
	, WRTER_EMPNO
	, WRITNG_TM)
VALUES(#{cardMenuNo}
, #{boardNo}
, #{cardNo}
, #{cardNm}
, #{wrterEmpno}
, SYSDATE)

mapper.update:
UPDATE
	TB_CARD
SET
	UPDT_EMPNO = #{updtEmpno}
	, UPDT_TM = SYSDATE
	, CARD_NM = #{cardNm}
WHERE
	CARD_MENU_NO = #{cardMenuNo}
	AND BOARD_NO = #{boardNo}
	AND CARD_NO = #{cardNo}

mapper.delete:
