FROM
	TB_CARD
WHERE
	CARD_MENU_NO = #{cardMenuNo}
	AND BOARD_NO = #{boardNo}
	AND CARD_NO = #{cardNo}

mapper.update:
UPDATE
	TB_CARD
SET
	FILE_NO = #{fileNo}
WHERE
	CARD_MENU_NO = #{cardMenuNo}
	AND BOARD_NO = #{boardNo}
	AND CARD_NO = #{cardNo}

mapper.select:
SELECT *
FROM TB_CARD A, TB_FILE B
WHERE A.FILE_NO = B.FILE_NO
AND A.FILE_NO = #{fileNo}
AND A.CARD_NO = #{cardNo}

mapper.delete:
DELETE
FROM
	TB_FILE
WHERE
	FILE_NO = #{fileNo}
AND FILE_ORDR = #{fileOrdr}

mapper.update:
UPDATE
	TB_CARD
SET
	CARD_CN = #{cardCn}
	, UPDT_EMPNO = #{updtEmpno}
	, UPDT_TM = SYSDATE
WHERE
	CARD_MENU_NO = #{cardMenuNo}
AND BOARD_NO = #{boardNo}
AND CARD_NO = #{cardNo}

UPDATE
	TB_CARD
SET
	CARD_END_DT = TO_DATE(#{cardEndDt}, 'YYYY-MM-dd')
	, UPDT_EMPNO = #{updtEmpno}
	, UPDT_TM = SYSDATE
WHERE
	CARD_MENU_NO = #{cardMenuNo}
AND BOARD_NO = #{boardNo}
AND CARD_NO = #{cardNo}

mapper.select:
SELECT COUNT(ANSWER_NO) AS answerCount 
FROM TB_CARD_ANSWER
WHERE CARD_NO = #{cardNo}

mapper.delete:
DELETE
FROM
	TB_CARD_ANSWER
WHERE
	CARD_MENU_NO = #{cardMenuNo}
	AND CARD_NO = #{cardNo}
	AND BOARD_NO = #{boardNo}
	AND ANSWER_NO = #{answerNo}

DELETE
FROM
	TB_CARD_ANSWER
WHERE
	CARD_MENU_NO = #{cardMenuNo}
	AND CARD_NO = #{cardNo}
	AND BOARD_NO = #{boardNo}

mapper.update:

SELECT ANSWER_NO AS answerNo FROM TB_CARD_ANSWER WHERE ANSWER_NO = #{answerNo}

UPDATE
	TB_CARD_ANSWER
SET
	UPDT_EMPNO = #{updtEmpno}
	, UPDT_TM = SYSDATE
	, ANSWER_CN = #{answerCn}
WHERE
	CARD_MENU_NO = #{cardMenuNo}
	AND CARD_NO = #{cardNo}
	AND BOARD_NO = #{boardNo}
	AND ANSWER_NO = #{answerNo}
